function WAF($xmlData) {
    $disallowed_strings = array("file", "SYSTEM", "PHP", "php", "ENTITY", "xxe", "XXE");
    foreach ($disallowed_strings as $string) {
        if (strpos(htmlentities($xmlData), $string) !== false) {
            return true; 
        }
    }
    return false; 
}

if ($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_SERVER['HTTP_X_REQUESTED_WITH']) && strtolower($_SERVER['HTTP_X_REQUESTED_WITH']) === 'xmlhttprequest') {
    $xmlData = file_get_contents('php://input');

    if (0) {
        http_response_code(400);
        exit("Potential exploit detected. Request blocked.");
    }

    $new_xml = simplexml_load_string($xmlData, null, LIBXML_NOENT);
    if ($new_xml !== false) {
        $output = "<h2>User Feedback:</h2>";
        $output .= "<ul>";
        $output .= "<li><strong>Name:</strong> " . htmlspecialchars($new_xml->name) . "</li>";
        $output .= "<li><strong>Email:</strong> " . htmlspecialchars($new_xml->email) . "</li>";
        $output .= "<li><strong>Message:</strong> " . htmlspecialchars($new_xml->message) . "</li>";
        $output .= "</ul>";

        echo $output; 
    } else {
        echo "Failed to parse XML.";
    }

    exit;
}
