<div class="container-fluid">
    <h1>Pune Metro Feedback</h1>

    <form id="feedbackForm">
        <div class="form-group">
            <label for="name">Name:</label>
            <input type="text" class="form-control" id="name" name="name" required>
        </div>

        <div class="form-group">
            <label for="email">Email Address:</label>
            <input type="email" class="form-control" id="email" name="email" required>
        </div>

        <div class="form-group">
            <label for="feedback">Feedback:</label>
            <textarea class="form-control" id="feedback" name="feedback" rows="4" required></textarea>
        </div>

        <button type="submit" class="btn btn-primary">Submit</button>
    </form>

    <div id="feedbackModal" style="display:none; position:fixed; z-index:1000; left:0; top:0; width:100%; height:100%; overflow:auto; background-color: rgba(0,0,0,0.4);">
    <div style="background-color: #fefefe; margin: 15% auto; padding: 20px; border: 1px solid #888; width: 80%;">
        <iframe id="feedbackResult" style="width:100%; height:s900px;"></iframe>
        <button id="confirmBtn" style="margin-top:10px;">Confirm</button>
    </div>
</div>
</div>

<script src="https://ajax.googleapis.com/ajax/libs/jquery/3.5.1/jquery.min.js"></script>
<script>
    $(document).ready(function() {
        $('#feedbackForm').submit(function(e) {
            e.preventDefault();
            var name = $('#name').val();
            var email = $('#email').val();
            var feedback = $('#feedback').val();

            var xmlData =
                '<feedback>' +
                '<name>' + name + '</name>' +
                '<email>' + email + '</email>' +
                '<message>' + feedback + '</message>' +
                '</feedback>';

            $.ajax({
                type: 'POST',
                url: '',
                data: xmlData,
                contentType: 'application/xml',
                success: function(response) {
                    var dataUrl = 'data:text/html;charset=utf-8,' + encodeURIComponent(response);
                    $('#feedbackResult').attr('src', dataUrl);
                    $('#feedbackModal').show(); 
                },
                error: function(error) {
                    alert('Error submitting feedback');
                }
            });
        });

        $('#confirmBtn').click(function() {
            $('#feedbackModal').hide();
            $('#feedbackResult').remove(); 
            location.reload(); 
        });
    });
</script>



